/*must have*/\n    height: 200px; /*must have*/\n}\n/* Dialog\n----------------------------------*/\n.ui-dialog { position: relative; padding: .2em; width: 300px; }\n.ui-dialog .ui-dialog-titlebar { padding: .5em .3em .3em 1em; position: relative;  }\n.ui-dialog .ui-dialog-title { float: left; margin: .1em 0 .2em; } \n.ui-dialog .ui-dialog-titlebar-close { position: absolute; right: .3em; top: 50%; width: 19px; margin: -10px 0 0 0; padding: 1px; height: 18px; }\n.ui-dialog .ui-dialog-titlebar-close span { display: block; margin: 1px; }\n.ui-dialog .ui-dialog-titlebar-close:hover, .ui-dialog .ui-dialog-titlebar-close:focus { padding: 0; }\n.ui-dialog .ui-dialog-content { border: 0; padding: .5em 1em; background: none; overflow: auto; }\n.ui-dialog .ui-dialog-buttonpane { text-align: left; border-width: 1px 0 0 0; background-image: none; margin: .5em 0 0 0; padding: .3em 1em .5em .4em; }\n.ui-dialog .ui-dialog-buttonpane button { float: right; margin: .5em .4em .5em 0; cursor: pointer; padding: .2em .6em .3em .6em; line-height: 1.4em; width:auto; overflow:visible; }\n.ui-dialog .ui-resizable-se { width: 14px; height: 14px; right: 3px; bottom: 3px; }\n.ui-draggable .ui-dialog-titlebar { cursor: move; }\n/* Progressbar\n----------------------------------*/\n.ui-progressbar { height:2em; text-align: left; }\n.ui-progressbar .ui-progressbar-value {margin: -1px; height:100%; }\n/* Resizable\n----------------------------------*/\n.ui-resizable { position: relative;}\n.ui-resizable-handle { position: absolute;font-size: 0.1px;z-index: 99999; display: block;}\n.ui-resizable-disabled .ui-resizable-handle, .ui-resizable-autohide .ui-resizable-handle { display: none; }\n.ui-resizable-n { cursor: n-resize; height: 7px; width: 100%; top: -5px; left: 0px; }\n.ui-resizable-s { cursor: s-resize; height: 7px; width: 100%; bottom: -5px; left: 0px; }\n.ui-resizable-e { cursor: e-resize; width: 7px; right: -5px; top: 0px; height: 100%; }\n.ui-resizable-w { cursor: w-resize; width: 7px; left: -5px; top: 0px; height: 100%; }\n.ui-resizable-se { cursor: se-resize; width: 12px; height: 12px; right: 1px; bottom: 1px; }\n.ui-resizable-sw { cursor: sw-resize; width: 9px; height: 9px; left: -5px; bottom: -5px; }\n.ui-resizable-nw { cursor: nw-resize; width: 9px; height: 9px; left: -5px; top: -5px; }\n.ui-resizable-ne { cursor: ne-resize; width: 9px; height: 9px; right: -5px; top: -5px;}\n/* Tabs\n----------------------------------*/\n.ui-tabs {padding: .2em;}\n.ui-tabs .ui-tabs-nav { padding: .2em .2em 0 .2em;  position: relative; }\n.ui-tabs .ui-tabs-nav li { float: left; border-bottom: 0 !important; margin: 0 .2em -1px 0; padding: 0; list-style: none; }\n.ui-tabs .ui-tabs-nav li a { display:block; text-decoration: none; padding: .5em 1em; }\n.ui-tabs .ui-tabs-nav li.ui-tabs-selected {  padding-bottom: .1em; border-bottom: 0; }\n.ui-tabs .ui-tabs-panel { padding: 1em 1.4em;  display: block; border: 0; background: none; }\n.ui-tabs .ui-tabs-hide { display: none !important; }\n\n/* themes/wind/css/screen.css */\n/**\n * Gallery 3 Wind Theme Screen Styles\n *\n * @requires YUI reset, font, grids CSS\n *\n * Sheet organization:\n *   1)  Font sizes, base HTML elements\n *   2)  Reusable content blocks\n *   3)  Page layout containers\n *   4)  Content blocks in specific layout containers\n *   5)  States and interactions\n *   6)  Positioning and order\n *   7)  Navigation and menus\n *   8)  jQuery and jQuery UI\n */\n\n/** *******************************************************************\n *  1) Font sizes, base HTML elements\n **********************************************************************/\n\nbody, html {\n  background-color: #ccc;\n  font-family: \'Lucida Grande\', \'Lucida Sans\', Arial, sans-serif;\n}\n\np {\n  margin-bottom: 1em;\n}\n\nem {\n  font-style: oblique;\n}\n\nh1, h2, h3, h4, h5, strong, th {\n  font-weight: bold;\n}\n\nh1 {\n  font-size: 1.7em;\n}\n\n#g-dialog h1 {\n  font-size: 1.1em;\n}\n\nh2 {\n  font-size: 1.4em;\n}\n\n#g-sidebar .g-block h2 {\n  font-size: 1.2em;\n}\n\n#g-sidebar .g-block li {\n  margin-bottom: .6em;\n}\n\n#g-content,\n#g-site-menu,\nh3 {\n  font-size: 1.2em;\n}\n\n#g-sidebar,\n.g-breadcrumbs {\n  font-size: .9em;\n}\n\n#g-banner,\n#g-footer,\n.g-message {\n  font-size: .8em;\n}\n\n#g-album-grid .g-item,\n#g-item #g-photo,\n#g-item #g-movie {\n  font-size: .7em;\n}\n\n/* Links ~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~ */\n\na,\n.g-menu a,\n#g-dialog a,\n.g-button,\n.g-button:hover,\n.g-button:active,\na.ui-state-hover,\ninput.ui-state-hover,\nbutton.ui-state-hover {\n  color: #5382bf !important;\n  cursor: pointer !important;\n  text-decoration: none;\n  -moz-outline-style: none;\n}\n\na:hover,\n#g-dialog a:hover {\n  text-decoration: underline;\n}\n\n.g-menu a:hover {\n  text-decoration: none;\n}\n\n#g-dialog #g-action-status li {\n  width: 434px;\n  white-space: normal;\n  padding-left: 32px;\n}\n\n/* Lists ~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~ */\n\nul.g-text li,\n.g-text ul li {\n  list-style-type: disc;\n}\n\nol.g-text li,\n.g-text ol li {\n  list-style-type: decimal;\n}\n\n.g-text li,\n.g-text li {\n  margin-left: 1em;\n}\n\n/* Forms ~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~ */\n\nform {\n  margin: 0;\n}\n\nfieldset {\n  border: 1px solid #ccc;\n  padding: 0 1em .8em 1em;\n}\n\n#g-banner fieldset,\n#g-sidebar fieldset {\n  border: none;\n  padding: 0;\n}\n\nlegend {\n  font-weight: bold;\n  margin: 0;\n  padding: 0 .2em;\n}\n\n#g-banner legend,\n#g-sidebar legend,\ninput[type="hidden"] {\n  display: none;\n}\n\ninput.textbox,\ninput[type="text"],\ninput[type="password"],\ntextarea {\n  border: 1px solid #e8e8e8;\n  border-top-color: #ccc;\n  border-left-color: #ccc;\n  clear: both;\n  color: #333;\n  width: 50%;\n}\n\ntextarea {\n  height: 12em;\n  width: 97%;\n}\n\ninput:focus,\ninput.textbox:focus,\ninput[type=text]:focus,\ntextarea:focus,\noption:focus {\n  background-color: #ffc;\n  color: #000;\n}\n\ninput.checkbox,\ninput[type=checkbox],\ninput.radio,\ninput[type=radio] {\n  float: left;\n  margin-right: .4em;\n}\n\n/* Form layout ~~~~~~~~~~~~~~~~~~~~~~~~~~~ */\n\nform li {\n  margin: 0;\n  padding: 0 0 .2em 0;\n}\n\nform ul {\n  margin-top: 0;\n}\n\nform ul ul {\n  clear: both;\n}\n\nform ul ul li {\n  float: left;\n}\n\ninput,\nselect,\ntextarea {\n  display: block;\n  clear: both;\n  padding: .2em;\n}\n\ninput[type="submit"],\ninput[type="reset"] {\n  display: inline;\n  clear: none;\n  float: left;\n}\n\n/* Forms in dialogs and panels ~~~~~~~~~ */\n\n#g-dialog ul li {\n  padding-bottom: .8em;\n}\n\n#g-dialog fieldset,\n#g-panel fieldset {\n  border: none;\n  padding: 0;\n}\n\n#g-panel legend {\n  display: none;\n}\n\ninput[readonly] {\n  background-color: #F4F4FC;\n}\n\n#g-dialog input.textbox,\n#g-dialog input[type=text],\n#g-dialog input[type=password],\n#g-dialog textarea {\n  width: 97%;\n}\n\n/* Short forms ~~~~~~~~~~~~~~~~~~~~~~~ */\n\n.g-short-form legend,\n.g-short-form label {\n  display: none;\n}\n\n.g-short-form fieldset {\n  border: none;\n  padding: 0;\n}\n\n.g-short-form li {\n  float: left;\n  margin: 0 !important;\n  padding: .4em 0;\n}\n\n.g-short-form .textbox,\n.g-short-form input[type=text] {\n  color: #666;\n  padding: .3em .6em;\n  width: 100%;\n}\n\n.g-short-form .textbox.g-error {\n  border: 1px solid #f00;\n  color: #f00;\n  padding-left: 24px;\n}\n\n.g-short-form .g-cancel {\n  display: block;\n  margin: .3em .8em;\n}\n\n#g-sidebar .g-short-form li {\n  padding-left: 0;\n  padding-right: 0;\n}\n\n/* Tables ~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~ */\n\ntable {\n  width: 100%;\n}\n\n#g-content table {\n  margin: 1em 0;\n}\n\ncaption,\nth {\n  text-align: left;\n}\n\nth,\ntd {\n  border: none;\n  border-bottom: 1px solid #ccc;\n  padding: .5em;\n}\n\ntd {\n  vertical-align: top;\n}\n\n/* Text ~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~ */\n\n.g-text-small {\n  font-size: .8em;\n}\n\n.g-text-big {\n  font-size: 1.2em;\n}\n\n.g-text-right {\n  text-align: right;\n}\n\n/** *******************************************************************\n *  2) Reusable content blocks\n *********************************************************************/\n\n.g-block h2 {\n  background-color: #e8e8e8;\n  padding: .3em .8em;\n}\n\n.g-block-content {\n  margin-top: 1em;\n}\n\n/** *******************************************************************\n *  3) Page layout containers\n *********************************************************************/\n\n/* Dimension and scale ~~~~~~~~~~~~~~~~~~~ */\n\n.g-one-quarter {\n  width: 25%;\n}\n\n.g-one-third {\n  width: 33%;\n}\n\n.g-one-half {\n  width: 50%;\n}\n\n.g-two-thirds {\n  width: 66%;\n}\n\n.g-three-quarters {\n  width: 75%;\n}\n\n.g-whole {\n  width: 100%;\n}\n\n/* View container ~~~~~~~~~~~~~~~~~~~~~~~~ */\n\n.g-view {\n  background-color: #fff;\n  border: 1px solid #ccc;\n  border-bottom: none;\n}\n\n/* Layout containers ~~~~~~~~~~~~~~~~~~~~~ */\n\n#g-header {\n  margin-bottom: 1em;\n}\n\n#g-banner {\n  background-color: #e8e8e8;\n  border-bottom: 1px solid #ccc;\n  min-height: 5em;\n  padding: 1em 20px;\n  position: relative;\n}\n\n#g-content {\n  padding-left: 20px;\n  position: relative;\n  width: 696px;\n}\n\n#g-sidebar {\n  padding: 0 20px;\n  width: 220px;\n}\n\n#g-footer {\n  background-color: #e8e8e8;\n  border-top: 1px solid #ccc;\n  margin-top: 20px;\n  padding: 10px 20px;\n}\n\n/** *******************************************************************\n *  4) Content blocks in specific layout containers\n *********************************************************************/\n\n/* Header  ~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~ */\n\n#g-banner #g-quick-search-form {\n  clear: right;\n  float: right;\n  margin-top: 1em;\n}\n\n#g-banner #g-quick-search-form input[type=\'text\'] {\n  width: 17em;\n}\n\n#g-content .g-block h2 {\n  background-color: transparent;\n  padding-left: 0;\n}\n\n/* Sidebar  ~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~ */\n\n#g-sidebar .g-block-content {\n  padding-left: 1em;\n}\n\n#g-sidebar #g-image-block {\n  overflow: hidden;\n}\n\n/* Album content ~~~~~~~~~~~~~~~~~~~~~~~~~ */\n\n#g-content #g-album-grid {\n  margin: 1em 0;\n  position: relative;\n  z-index: 1;\n}\n\n#g-content #g-album-grid .g-item {\n  background-color: #fff;\n  border: 1px solid #fff;\n  float: left;\n  padding: .6em 8px;\n  position: relative;\n  text-align: center;\n  width: 213px;\n  z-index: 1;\n}\n\n#g-content #g-album-grid .g-item h2 {\n  margin: 5px 0;\n}\n\n#g-content .g-photo h2,\n#g-content .g-item .g-metadata {\n  display: none;\n  margin-bottom: .6em;\n}\n\n#g-content #g-album-grid .g-album {\n  background-color: #e8e8e8;\n}\n\n#g-content #g-album-grid .g-album h2 span.g-album {\n  background: transparent url(\'http://www.lmee.net/gallery/themes/wind/css/../images/ico-album.png\') no-repeat top left;\n  display: inline-block;\n  height: 16px;\n  margin-right: 5px;\n  width: 16px;\n}\n\n#g-content #g-album-grid .g-hover-item {\n  border: 1px solid #000;\n  position: absolute !important;\n  z-index: 1000 !important;\n}\n\n#g-content .g-hover-item h2,\n#g-content .g-hover-item .g-metadata {\n  display: block;\n}\n\n#g-content #g-album-grid #g-place-holder {\n  position: relative;\n  visibility: hidden;\n  z-index: 1;\n}\n\n/* Search results ~~~~~~~~~~~~~~~~~~~~~~~~ */\n\n#g-content #g-search-results {\n  margin-top: 1em;\n  padding-top: 1em;\n}\n\n/* Individual photo content ~~~~~~~~~~~~~~ */\n\n#g-item {\n  position: relative;\n  width: 100%;\n}\n\n#g-item #g-photo,\n#g-item #g-movie {\n  padding: 2.2em 0;\n  position: relative;\n}\n\n#g-item img.g-resize,\n#g-item a.g-movie {\n  display: block;\n  margin: 0 auto;\n}\n\n/* Footer content ~~~~~~~~~~~~~~~~~~~~~~~~ */\n\n#g-footer #g-credits li {\n  padding-right: 1.2em;\n}\n\n/* In-line editing  ~~~~~~~~~~~~~~~~~~~~~~ */\n\n#g-in-place-edit-message {\n  background-color: #fff;\n}\n\n/* Permissions ~~~~~~~~~~~~~~~~~~~~~~~~~~~ */\n\n#g-edit-permissions-form td {\n  background-image: none;\n}\n\n#g-edit-permissions-form fieldset {\n  border: 1px solid #ccc;\n}\n\n#g-permissions .g-denied {\n  background-color: #fcc;\n}\n\n#g-permissions .g-allowed {\n  background-color: #cfc;\n}\n\n#g-permissions .g-breadcrumbs a {\n  border: 1px solid #fff;\n}\n\n#g-permissions .g-active a {\n  border: 1px solid #ddd;\n  background: #eee;\n}\n\n/** *******************************************************************\n *  5) States and interactions\n **********************************************************************/\n\n.g-active,\n.g-enabled,\n.g-available,\n.g-selected,\n.g-highlight {\n  font-weight: bold;\n}\n\n.g-inactive,\n.g-disabled,\n.g-unavailable,\n.g-uneditable,\n.g-locked,\n.g-deselected,\n.g-understate {\n  color: #ccc;\n  font-weight: normal;\n}\n\n.g-editable {\n  padding: .2em .3em;\n}\n\n.g-editable:hover {\n  background-color: #ffc;\n  cursor: text;\n}\n\n.g-error,\n.g-info,\n.g-success,\n.g-warning {\n  padding-left: 30px;\n}\n\nform li.g-error,\nform li.g-info,\nform li.g-success,\nform li.g-warning {\n  background-image: none;\n  padding: .3em .8em .3em 0;\n}\n\n.g-short-form li.g-error {\n  padding: .3em 0;\n}\n\nform.g-error input[type="text"],\nli.g-error input[type="text"],\nform.g-error input[type="password"],\nli.g-error input[type="password"],\nform.g-error input[type="checkbox"],\nli.g-error input[type="checkbox"],\nform.g-error input[type="radio"],\nli.g-error input[type="radio"],\nform.g-error textarea,\nli.g-error textarea,\nform.g-error select,\nli.g-error select {\n  border: 2px solid #f00;\n  margin-bottom: .2em;\n}\n\n.g-error,\n.g-denied,\ntr.g-error td.g-error,\n#g-add-photos-status .g-error {\n  background: #f6cbca url(\'http://www.lmee.net/gallery/themes/wind/css/../images/ico-error.png\') no-repeat .4em 50%;\n  color: #f00;\n}\n\n.g-info {\n  background: #e8e8e8 url(\'http://www.lmee.net/gallery/themes/wind/css/../images/ico-info.png\') no-repeat .4em 50%;\n}\n\n.g-success,\n.g-allowed,\n#g-add-photos-status .g-success {\n  background: #d9efc2 url(\'http://www.lmee.net/gallery/themes/wind/css/../images/ico-succes