INSERT IGNORE INTO `ga_caches` VALUES (14, '7708d672c52a57c249cf69da4a195655', '<css>', 1363005832, 's:62925:"/* modules/gallery/css/gallery.css */\n/**\n * Gallery 3 core module styles\n *\n * Sheet organization:\n *  1)  End-user\n *  2)  Admin\n *  3)  Right to left language styles\n */\n\n/** *******************************************************************\n * 1) End-user\n **********************************************************************/\n\n/* Uploader ~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~ */\n\n#g-add-photos-canvas,\n#g-add-photos-status {\n  width: 469px;\n}\n\n#g-add-photos-canvas {\n  border: 1px solid  #ccc;\n  height: 200px;\n  margin: .5em 0;\n  padding: 2.8em 0 0 0;\n  overflow: auto;\n  position: relative;\n}\n\n#g-add-photos-canvas object,\n#g-add-photos-button {\n  left: 90px;\n  margin: .5em 0;\n  padding: .4em 1em;\n  position: absolute;\n  top: 0;\n  width: 300px;\n}\n\n#g-add-photos-canvas object {\n  z-index: 100;\n}\n\n#g-add-photos-canvas .uploadifyQueue {\n  margin-top: .5em;\n}\n\n#g-add-photos-canvas .uploadifyQueueItem {\n  margin: 0;\n}\n\n#g-add-photos-button {\n  z-index: 1;\n}\n\n#g-add-photos-status {\n  border: 1px solid #ccc;\n  height: 125px;\n  margin: .4em 0;\n  overflow: auto;\n}\n\n#g-add-photos-status .g-message-block {\n  border: none;\n}\n\n#g-add-photos-status #g-action-status li {\n  margin: 0 0 1px 0;\n  padding-top: .7em;\n  width: 433px;\n}\n\n#g-add-photos-form .g-breadcrumbs {\n  margin: 0;\n}\n\n#g-add-photos-form p {\n  margin-bottom: 0\n}\n\n#g-add-photos-status-message {\n  float: right;\n}\n\n/* Permissions ~~~~~~~~~~~~~~~~~~~~~~~~~~~~ */\n\n#g-edit-permissions-form {\n  clear: both;\n}\n\n#g-edit-permissions-form th {\n  text-align: center;\n}\n\n#g-permissions .g-denied,\n#g-permissions .g-allowed {\n  text-align: center;\n  vertical-align: middle;\n}\n\n/* Move items ~~~~~~~~~~~~~~~~~~~~~~~~~~~~ */\n\n#g-move ul {\n  padding-left: 1em;\n}\n\n#g-move .selected {\n  background: #999;\n}\n\n/* In-place edit ~~~