ock;\n  border: 1px solid #999;\n  margin: 0 1em 1em 0;\n  padding: 0;\n  width: 200px;\n}\n\n.g-group h4 {\n  background-color: #eee;\n  border-bottom: 1px dashed #ccc;\n  padding: .5em 0 .5em .5em;\n}\n\n.g-group .g-button {\n  padding: 0;\n}\n\n.g-group .g-member-list,\n.g-group div {\n  height: 180px;\n  margin: 1px;\n  overflow: auto;\n}\n\n.g-group p {\n  margin-top: 1em;\n  padding: .5em;\n  text-align: center;\n}\n\n.g-group .g-user {\n  padding: .2em 0 0 .5em;\n}\n\n.g-group .g-user .g-button {\n  vertical-align: middle;\n}\n\n.g-default-group h4,\n.g-default-group .g-user {\n  color: #999;\n}\n\n.g-group.ui-droppable {\n  padding: 0 !important;\n}\n\n/* Password strength meter ~~~~~~~~~~~~~~~ */\n\n.g-password-strength0 {\n  background: url(\'http://www.lmee.net/gallery/modules/user/css/../images/progressImg1.png\') no-repeat 0 0;\n  width: 138px;\n  height: 7px;\n}\n\n.g-password-strength10 {\n  background-position:0 -7px;\n}\n\n.g-password-strength20 {\n  background-position:0 -14px;\n}\n\n.g-password-strength30 {\n  background-position:0 -21px;\n}\n\n.g-password-strength40 {\n  background-position:0 -28px;\n}\n\n.g-password-strength50 {\n  background-position:0 -35px;\n}\n\n.g-password-strength60 {\n  background-position:0 -42px;\n}\n\n.g-password-strength70 {\n  background-position:0 -49px;\n}\n\n.g-password-strength80 {\n  background-position:0 -56px;\n}\n\n.g-password-strength90 {\n  background-position:0 -63px;\n}\n\n.g-password-strength100 {\n  background-position:0 -70px;\n}\n\n/* modules/comment/css/comment.css */\n#g-content #g-comment-form {\n  margin-top: 2em;\n}\n\n#g-content #g-comments {\n  margin-top: 2em;\n  position: relative;\n}\n\n#g-content #g-comments ul li {\n  margin: 1em 0;\n}\n\n#g-content #g-comments .g-author {\n  border-bottom: 1px solid #ccc;\n  color: #999;\n  height: 32px;\n  line-height: 32px;\n}\n\n#g-content #g-comments ul li div {\n  padding: 0 8px 8px 43px;\n}\n\n#g-content #g-comments .g-avatar {\n  height: 32px;\n  margin-right: .4em;\n  width: 32px;\n}\n\n#g-add-comment {\n  position: absolute;\n  right: 0;\n  top: 2px;\n}\n\n#g-admin-comments-menu {\n  margin: 1em 0;\n}\n\n#g-admin-comments-menu a {\n  margin: 0;\n  padding: .2em .6em;\n}\n\n\n/* lib/jquery.autocomplete.css */\n.ac_results {\n	padding: 0px;\n	border: 1px solid black;\n	background-color: white;\n	overflow: hidden;\n	z-index: 99999;\n	text-align: left;\n}\n\n.ac_results ul {\n	width: 100%;\n	list-style-position: outside;\n	list-style: none;\n	padding: 0;\n	margin: 0;\n}\n\n.ac_results li {\n	margin: 0px;\n	padding: 2px 5px;\n	cursor: default;\n	display: block;\n	/* \n	if width will be 100% horizontal scrollbar will apear \n	when scroll mode will be used\n	*/\n	/*width: 100%;*/\n	font: menu;\n	font-size: 12px;\n	/* \n	it is very important, if line-height not setted or setted \n	in relative units scroll will be broken in firefox\n	*/\n	line-height: 16px;\n	overflow: hidden;\n}\n\n.ac_loading {\n	background: white url(\'http://www.lmee.net/gallery/lib/images/loading-small.gif\') right center no-repeat;\n}\n\n.ac_odd {\n	background-color: #eee;\n}\n\n.ac_over {\n	background-color: #0A246A;\n	color: white;\n}\n\n/* modules/tag/css/tag.css */\n/* Tag cloud ~~~~~~~~~~~~~~~~~~~~~~~ */\n\n#g-tag-cloud ul {\n  font-size: 1.2em;\n  text-align: justify;\n}\n\n#g-tag-cloud ul li {\n  display: inline;\n  line-height: 1.5em;\n  text-align: justify;\n}\n\n#g-tag-cloud ul li a {\n  text-decoration: none;\n}\n\n#g-tag-cloud ul li span {\n  display: none;\n}\n\n#g-tag-cloud ul li.size0 a {\n  color: #9cf;\n  font-size: 70%;\n  font-weight: 100;\n}\n\n#g-tag-cloud ul li.size1 a {\n  color: #9cf;\n  font-size: 80%;\n  font-weight: 100;\n}\n\n#g-tag-cloud ul li.size2 a {\n  color: #69f;\n  font-size: 90%;\n  font-weight: 300;\n}\n\n#g-tag-cloud ul li.size3 a {\n  color: #69c;\n  font-size: 100%;\n  font-weight: 500;\n}\n\n#g-tag-cloud ul li.size4 a {\n  color: #369;\n  font-size: 110%;\n  font-weight: 700;\n}\n\n#g-tag-cloud ul li.s